import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Delete = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const fetchAllBooks = async () => {
      try {
        const res = await axios.get("http://localhost:8000/deletedetails");
        setBooks(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllBooks();
  }, []);

  console.log(books);

  const handleDelete = async (postnumber) => {
    try {
        console.log('delete')

      //await axios.delete(`http://localhost:8000/books/${postnumber}`);
      fetch(`http://localhost:8000/update`,{
        method: 'POST',
                                    headers: {
                                        "Content-Type": "application/json",
                                      },
                                      body: JSON.stringify({postnumber}),
                                    
                                  
      })
      console.log('delete')
      console.log({postnumber})
      
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div>
      <h1>delete post</h1>
      <div className="books">
        {books.map((post) => (
          <div key={post.postnumber} className="book">
            <img src={post.detail} alt="" />
            <h2>{post.detail}</h2>
      
            <button className="update">
              <Link
                to={`/update/update1`}
                style={{ color: "inherit", textDecoration: "none" }}
              >
                Update
              </Link>
            </button>
            
          </div>
        ))}
      </div>

      
    </div>
  );
};

export default Delete;
