import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Books = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const fetchAllBooks = async () => {
      try {
        const res = await axios.get("http://localhost:8000/books");
        setBooks(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllBooks();
  }, []);

  console.log(books);

  const handleDelete = async (postnumber) => {
    try {
      await axios.delete(`http://localhost:8000/books/${postnumber}`);
      console.log('delete')
      window.location.reload()
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div>
      <h1>delete post</h1>
      <div className="books">
        {books.map((book) => (
          <div key={book.postnumber} className="book">
            <img src={book.detail} alt="" />
            <h2>{book.detail}</h2>
      
            <button className="delete" onClick={() => handleDelete(book.postnumber)}>Delete</button>
            <button className="update">
              <Link
                to={`/update/${book.postnumber}`}
                style={{ color: "inherit", textDecoration: "none" }}
              >

                Update
              </Link>
            </button>
          </div>
        ))}
      </div>

      
    </div>
  );
};

export default Books;
