import React from "react";
import allproducts from "./allproducts";
import {Link, Outlet} from "react-router-dom";
function ViewProducts()
{
    function Card(props)
    {
        return(
                <tr>
                    <td>{props.productNo}</td>
                    <td>{props.productName}</td>
                    <td>{props.Mrp}</td>
                    <td>{props.Stock}</td>
                </tr>
        )
    }
    function createCard(allproducts)
    {
        return <Card 
                    key={allproducts.id}
                    productNo={allproducts.productNo}
                    productName={allproducts.productName}
                    Mrp={allproducts.Mrp}
                    Stock={allproducts.Stock}
                />  
    }
    return(
        <div className="mainview">
        <div className="viewhead">
            <h1>View Products</h1>
        </div>
        <div className="products">
            <table>
                <tr>
                    <th>Product No</th>
                    <th>Product Name</th>
                    <th>Mrp</th>
                    <th>Stock</th>
                </tr>
                {allproducts.map(createCard)}

            </table>
        </div>
        
       </div>
    )
}

export default ViewProducts;
